	public static SVGPath getPlatformIcon(String platformName) {
		SVGPath path = new SVGPath();

		// Usamos el switch para determinar qu√© path asignar
		switch (platformName.toLowerCase()) {
		case "PlayStation 5":
			path.setContent(PLAYSTATION);
			break;
		case "Xbox Series S/X":
			path.setContent(XBOX);
			break;
		case "Nintendo Switch":
			path.setContent(NINTENDO);
			break;
		case "PC":
			path.setContent(PC);
			break;
		default:
			path.setContent(DEFAULT);
			break;
		}

		return path;
	}
